import React from 'react';
import './SignUp.css';

class SignUp extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            email: "mon@email.com",
            password: "monPassw0rd",
            irstName: "James",
            lastName: "Bond"

            // email: "",
            // password: "",
            // irstName: "",
            // lastName: ""
    };


        this.updateEmailField=this.updateEmailField.bind(this);
        this.updatelastNameField=this.updatelastNameField.bind(this);
        this.submitForm = this.submitForm.bind(this);
}



      updateEmailField(event){
        this.setState({email: event.target.value});
      }

      updatelastNameField(event){
        this.setState({lastName: event.target.value});
      }

      submitForm(e) {
        e.preventDefault();
        JSON.stringify(this.state,1,1)
        }



  render() {
    return(
    <div>
        <h1 className='title'>SignUp</h1>
        <h1 className='dsp'> Hello, {this.state.lastName}</h1>
        <form className='formfields'>
            <input className='email' value={this.state.email} placeholder="E-mail" type="email" name="email"/>
            <input className='email' value={this.state.password} placeholder="Password" type="password" name="password"/>
            <input className='email' value={this.state.password} placeholder="Confirm Password" type="password" name="password"/>
            <input className='email' value={this.state.firstName} placeholder="First Name" type="firstName" name="firstName"/>
            <input className='email' value={this.state.lastName} onChange={this.updatelastNameField} placeholder="Last Name" type="lastName" name="lastName"/>
            <input className='btn' onSubmit={this.submitForm} type="submit" value="Submit"/>
        </form>

    </div>
    )
  }
}

export default SignUp;
